 function rem(){
        var uname = document.getElementById('uname').value;
        var paswd = document.getElementById('paswd').value;
        var rem   = document.getElementById('remember').checked;
    
        if(rem == true){
        var date = new Date();
        date.setTime(date.getTime()+(5*1000));
        var expires = date.toGMTString();
        date.setTime(date.getTime()+(259200*1000)); // 3 days
        var expire  = date.toGMTString();
        document.cookie = 'uname='+uname+';expires='+expire+';path=/'; // Added path
        document.cookie = 'paswd='+paswd+';expires='+expire+';path=/'; // Added path
        document.cookie = 'error=1;expires='+expires+';path=/';      // Added path
        }
        else{
        // Clear cookies if not remember me
         document.cookie = "uname=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";
         document.cookie = "paswd=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";
        return;
        }
        }
    
        function getCookie(){
    
        var date = new Date();
        date.setTime(date.getTime()+(5*1000)); // 5 seconds
        var expires = date.toGMTString();
    
        var SP = document.cookie.split(';');
        var uname = ""; // Initialize
        var paswd = ""; // Initialize

        for (i=0; i < SP.length; i++) {
            var name_value = SP[i].trim(); // Trim whitespace
            var pair = name_value.split('=');
            if(pair[0] == "uname"){
                uname = pair[1] ? decodeURIComponent(pair[1]) : "";
            }
            if(pair[0] == "paswd"){
                paswd = pair[1] ? decodeURIComponent(pair[1]) : "";
            }
            if(pair[0] == "error"){ // If error cookie exists, means we already tried auto-login
                return;
            }
        }
    
        if(uname != "" && paswd != ""){ // Check if both are found
            document.cookie = 'error=1;expires='+expires+';path=/'; // Set error cookie to prevent loop
            // Ensure username and password are URL encoded for the redirect
            window.location.href='login?username='+encodeURIComponent(uname)+'&password='+encodeURIComponent(paswd)+'';
        }
    
        }
    
       getCookie();